import React from "react";
import styles from "./CardItem.module.css";


const CardItem = props => {
  const bg = props.source;
  const imgStle = [img-fluid, styles.imgSize ]

  return (
    <div className="my-3">
        <h3>{props.name}</h3>
        <img className="img-fluid" alt="" src={bg} />
    </div>
  );
};

export default CardItem;
