import React from "react";
import styles from "./CardItem.css";

const Dots = props => {
    const dotsClasses = [styles.dots];
    
    if (props.isDotActive) {
        dotsClasses.push(styles.isActive);
    }
    
    return (
        <div onClick={props.clickDots}  className={dotsClasses.join(' ')} >

        </div>
    );
};

export default Dots;
