import React from "react";
import styles from "./CardItem.module.css";

const Dots = props => {
    const dotsClasses = [styles.dots].join(' ');
    
    if (props.isDotActive) {
        dotsClasses.push[styles.isActive];
    }
    
    return (
        <div className={dotsClasses} isACtive={props.active} >

        </div>
    );
};

export default Dots;
