import React, { Component } from "react";
import './App.css';
import CardItem from "./components/Carousel/CardItems/CardItem";
import Dots from "./components/Carousel/CarouselDots/Dots";
import 'bootstrap/dist/css/bootstrap.min.css';
import Arrows from './components/Carousel/CarouselArrows/Arrows';
import FirstLetter from './components/Carousel/CarouselDots/DisplayFirstLetter';


class App extends Component {
    constructor(props) {
        
    }
    
    

   
    render() {
        let items = null;
        let renderDots = null;
        let backgroundImg = null;

        items = (
            <div className="w-75">
                {this.state.cards.map((item, index) => {

                    // return only the item that has the index === slideIndex
                    if (index === this.state.slideIndex ) {
                        backgroundImg = item.url;
                        
                        return (
                            <div>
                                <CardItem 
                                    name={item.name} 
                                    key={item.id} 
                                    category={item.category}
                                    cardId={item.id}
                                />

                                
                            </div>
                        );
                    }
                })}

                <Arrows 
                    moveSliderRight={() => this.handleClickRightArrow()}
                    moveSliderLeft={() => this.handleClickLeftArrow()}
                />
            </div>
        );

        const bgImg = {
            backgroundImage: `url(${backgroundImg})`,
            backgroundPosition: 'center',
            backgroundSize: 'cover',
            backgroundRepeat: 'no-repeat',
        }
        
        renderDots = (
            <div className="dots-wrapper">
                {this.state.cards.map((item, index) => {
                    return (
                        <Dots 
                            isDotActive={index === this.state.slideIndex ? true : false}      
                            clickDots={() => this.handleClickDots(index)}
                            key={item.id}
                            letter
                        />
                    );
                })}
            </div>
        );
        
        return (
            <div className="carousel-wrapper position-relative">
                <h1>hello world</h1>
            </div>
        );
    }
}

export default App;
